// src/pages/StudentRegistration.js

import React, { useState } from "react";
import axios from "axios";
import "./../styles/StudentRegistration.css";

const StudentRegistration = ({ handleRegistration }) => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!name || !email) {
      setError("Please fill in all fields");
      return;
    }

    // Send registration data to the server
    axios
      .post("http://localhost:5000/register", { name, email })
      .then((response) => {
        // Registration successful, call handleRegistration
        handleRegistration(); // Notify the parent component (App.js) that the user is registered
      })
      .catch((error) => {
        setError("Registration failed. Please try again.");
      });
  };

  return (
    <div className="registration-container">
      <h2>Student Registration</h2>
      {error && <p className="error">{error}</p>}
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Name</label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Enter your name"
          />
        </div>
        <div className="form-group">
          <label>Email</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Enter your email"
          />
        </div>
        <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default StudentRegistration;
