import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Home from "./pages/Home";
import Game from "./pages/Game";
import DrawLearn from "./pages/DrawLearn";
import PuzzleMatch from "./pages/PuzzleMatch";
import MathPlayground from "./pages/MathPlayground";
import StoryMode from "./pages/StoryMode";
import Leaderboard from "./pages/Leaderboard";
import VideoLearning from "./pages/VideoLearning";
import Dashboard from "./pages/Dashboard";
import Navbar from "./pages/Navbar";
import StudentRegistration from "./pages/StudentRegistration"; // Import Registration page
import "./App.css";

function App() {
  const [isRegistered, setIsRegistered] = useState(false); // Track registration status

  const handleRegistration = () => {
    setIsRegistered(true); // Set to true once the user is registered
  };

  return (
    <Router>
      <Navbar /> {/* Display Navbar */}

      <Routes>
        <Route path="/" element={isRegistered ? <Home /> : <Navigate to="/register" />} />
        <Route path="/game" element={isRegistered ? <Game /> : <Navigate to="/register" />} />
        <Route path="/draw-learn" element={isRegistered ? <DrawLearn /> : <Navigate to="/register" />} />
        <Route path="/puzzle-match" element={isRegistered ? <PuzzleMatch /> : <Navigate to="/register" />} />
        <Route path="/math-playground" element={isRegistered ? <MathPlayground /> : <Navigate to="/register" />} />
        <Route path="/story-mode" element={isRegistered ? <StoryMode /> : <Navigate to="/register" />} />
        <Route path="/leaderboard" element={isRegistered ? <Leaderboard /> : <Navigate to="/register" />} />
        <Route path="/video-learning" element={isRegistered ? <VideoLearning /> : <Navigate to="/register" />} />
        <Route path="/dashboard" element={isRegistered ? <Dashboard /> : <Navigate to="/register" />} />
        <Route path="/register" element={<StudentRegistration handleRegistration={handleRegistration} />} />
      </Routes>
    </Router>
  );
}

export default App;
