// src/pages/PuzzleMatch.js
import React, { useState } from "react";
import "./../styles/PuzzleMatch.css";

const images = [
  { id: 1, src: "./../images/apple.png", name: "Apple" },
  { id: 2, src: "./../images/banana.png", name: "Banana" },
  { id:3, src: "./../images/car.png", name: "Car" },
  { id:4, src: "./../images/dog.png", name: "Dog" }
];

const PuzzleMatch = () => {
  const [shuffledImages] = useState([...images].sort(() => Math.random() - 0.5));
  const [selectedPair, setSelectedPair] = useState([]);
  const [matchedPairs, setMatchedPairs] = useState([]);

  const handleSelection = (item) => {
    if (selectedPair.length === 1 && selectedPair[0].id === item.id) return;

    const newSelection = [...selectedPair, item];

    if (newSelection.length === 2) {
      if (newSelection[0].id === newSelection[1].id) {
        setMatchedPairs([...matchedPairs, newSelection[0].id]);
      }
      setTimeout(() => setSelectedPair([]), 800);
    } else {
      setSelectedPair(newSelection);
    }
  };

  return (
    <div className="puzzle-container">
      <h1>Match the Pairs</h1>
      <p>Find the matching image and name!</p>
      <div className="puzzle-grid">
        {shuffledImages.map((item, index) => (
          <div
            key={index}
            className={`puzzle-item ${
              selectedPair.includes(item) || matchedPairs.includes(item.id) ? "flipped" : ""
            }`}
            onClick={() => handleSelection(item)}
          >
            <div className="puzzle-front">
              <img src={item.src} alt={item.name} />
            </div>
            <div className="puzzle-back">
              {item.name}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PuzzleMatch;
