// src/components/AIHelper.js

const AIHelper = {
    getQuestion: (category) => {
      const questions = {
        Math: {
          text: "What is 5 + 7?",
          correct: "12",
          audio: "/audio/math-1.mp3",
          difficulty: "easy",
        },
        Spelling: {
          text: "Spell the word 'education'.",
          correct: "education",
          audio: "/audio/spelling-1.mp3",
          difficulty: "medium",
        },
        Shapes: {
          text: "How many sides does a triangle have?",
          correct: "3",
          audio: "/audio/shapes-1.mp3",
          difficulty: "easy",
        },
        Colors: {
          text: "What color is the sky on a clear day?",
          correct: "blue",
          audio: "/audio/colors-1.mp3",
          difficulty: "easy",
        },
      };

      return questions[category] || { text: "No question available", correct: "" };
    },

    getDifficulty: (category) => {
      return this.getQuestion(category).difficulty;
    },
  };

  export default AIHelper;
