import React, { useState } from "react";
import "./SpinWheel.css";

const SpinWheel = ({ onSpin }) => {
  const [spinning, setSpinning] = useState(false);
  const categories = ["Math", "Spelling", "Shapes", "Colors"];

  const questions = {
    Math: {
      question: "What is 2 + 2?",
      options: ["3", "4", "5", "6"]
    },
    Spelling: {
      question: "How do you spell 'Cat'?",
      options: ["C-A-T", "C-T-A", "K-A-T", "C-A-T-T"]
    },
    Shapes: {
      question: "What shape is a ball?",
      options: ["Circle", "Square", "Triangle", "Rectangle"]
    },
    Colors: {
      question: "What color is the sky?",
      options: ["Blue", "Green", "Red", "Yellow"]
    }
  };

  const spinWheel = () => {
    if (!spinning) {
      setSpinning(true);
      const deg = Math.floor(Math.random() * 360) + 720; // Minimum 2 full rotations
      document.getElementById("wheel").style.transition = "transform 4s ease-out";
      document.getElementById("wheel").style.transform = `rotate(${deg}deg)`;

      // After spinning completes
      setTimeout(() => {
        const selectedCategory = categories[Math.floor(Math.random() * categories.length)];
        const question = questions[selectedCategory]; // Get the corresponding question object
        onSpin(question); // Pass the question object to the parent
        setSpinning(false);
      }, 3000); // Duration of spin (4s)
    }
  };

  return (
    <div className="wheel-container">
      <div className="wheel" id="wheel"></div>
      <button className="spin-button" onClick={spinWheel}>
        Spin the Wheel ðŸŽ¡
      </button>
    </div>
  );
};

export default SpinWheel;
