import React, { useState, useEffect } from "react";
import "./../styles/PuzzleMatch.css";

const items = [
  { id: 1, src: "/images/apple.png", name: "Apple", type: "image" },
  { id: 2, name: "Apple", type: "text" },
  { id: 3, src: "/images/banana.png", name: "Banana", type: "image" },
  { id: 4, name: "Banana", type: "text" },
  { id: 5, src: "/images/car.png", name: "Car", type: "image" },
  { id: 6, name: "Car", type: "text" },
  { id: 7, src: "/images/dog.png", name: "Dog", type: "image" },
  { id: 8, name: "Dog", type: "text" },
];

const PuzzleMatch = () => {
  const [shuffledItems, setShuffledItems] = useState([]);
  const [selectedPair, setSelectedPair] = useState([]);
  const [matchedPairs, setMatchedPairs] = useState([]);
  const [isChecking, setIsChecking] = useState(false);

  // Shuffle items on mount
  useEffect(() => {
    setShuffledItems([...items].sort(() => Math.random() - 0.5));
  }, []);

  const handleSelection = (item) => {
    if (isChecking || selectedPair.some((selected) => selected.id === item.id) || matchedPairs.includes(item.name)) {
      return;
    }

    const newSelection = [...selectedPair, item];
    setSelectedPair(newSelection);

    if (newSelection.length === 2) {
      setIsChecking(true);

      setTimeout(() => {
        if (newSelection[0].name === newSelection[1].name && newSelection[0].type !== newSelection[1].type) {
          setMatchedPairs((prev) => [...prev, newSelection[0].name]);
        }
        setSelectedPair([]);
        setIsChecking(false);
      }, 1000);
    }
  };

  return (
    <div className="puzzle-container">
      <h1>Match the Pairs</h1>
      <p>Find the matching image and text!</p>
      <div className="puzzle-grid">
        {shuffledItems.map((item) => {
          const isFlipped = selectedPair.some((selected) => selected.id === item.id) || matchedPairs.includes(item.name);

          return (
            <div
              key={item.id}
              className={`puzzle-item ${isFlipped ? "flipped" : ""} ${matchedPairs.includes(item.name) ? "matched" : ""}`}
              onClick={() => handleSelection(item)}
            >
              <div className="puzzle-inner">
                <div className="puzzle-front">{isFlipped ? (item.type === "image" ? <img src={item.src} alt={item.name} /> : <span>{item.name}</span>) : "?"}</div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default PuzzleMatch;
