import React, { useState, useEffect } from "react";
import AIHelper from "../components/AIHelper";
import SpinWheel from "../components/SpinWheel";
import "./../styles/Game.css";

const Game = () => {
  const [question, setQuestion] = useState(null);
  const [answer, setAnswer] = useState("");
  const [feedback, setFeedback] = useState(null);

  useEffect(() => {
    if (question) {
      // Example: Show visual feedback for the question
      setFeedback(null); // Reset feedback before every question
    }
  }, [question]);

  const handleSpinResult = (category) => {
    setQuestion(AIHelper.getQuestion(category));
  };

  const checkAnswer = () => {
    if (answer === question.correct) {
      setFeedback("üéâ Correct! Well done!");
    } else {
      setFeedback("‚ùå Oops! Try again.");
    }
  };

  return (
    <div className="game-container">
      <h1>üé° Spin & Answer!</h1>
      <SpinWheel onSpin={handleSpinResult} />
      {question && (
        <div className="question-box">
          <h2>{question.text}</h2>
          <input type="text" onChange={(e) => setAnswer(e.target.value)} />
          <button onClick={checkAnswer}>Submit</button>
        </div>
      )}
      {feedback && <div className="feedback">{feedback}</div>}
    </div>
  );
};

export default Game;
