// src/pages/Game.js
import React, { useState } from "react";
import "./../styles/Game.css";
import SpinWheel from "../components/SpinWheel";

const Game = () => {
  const [showQuestion, setShowQuestion] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState(null);
  const [userAnswer, setUserAnswer] = useState(null);
  const [feedback, setFeedback] = useState(""); // For feedback message

  const handleSpinResult = (question) => {
    setCurrentQuestion(question);
    setShowQuestion(true);
    setUserAnswer(null); // Reset user answer when a new question is shown
    setFeedback(""); // Reset feedback message
  };

  const handleAnswerClick = (answer) => {
    setUserAnswer(answer); // Set the user's answer
    // Check if the answer is correct
    if (answer === currentQuestion.correctAnswer) {
      setFeedback("Correct! ðŸŽ‰");
    } else {
      setFeedback("Wrong answer. Try again! ðŸ˜•");
    }
  };

  return (
    <div className="game-container">
      <h1 className="game-title">Spin & Learn</h1>
      <div className="game-content">
        <div className="spin-wheel-container">
          <SpinWheel onSpin={handleSpinResult} />
        </div>
        <div className="question-container">
          {showQuestion && currentQuestion && (
            <div className="question-box">
              <h2>{currentQuestion.question}</h2>
              <ul>
                {currentQuestion.options.map((option, index) => (
                  <li
                    key={index}
                    className={userAnswer === option ? "selected" : ""}
                    onClick={() => handleAnswerClick(option)}
                  >
                    {option}
                  </li>
                ))}
              </ul>
              <div className={`feedback ${feedback === "Correct! ðŸŽ‰" ? "correct" : "wrong"}`}>
                {feedback}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Game;
