import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";
import "./../styles/Game.css";
import SpinWheel from "../components/SpinWheel";

const socket = io("http://localhost:5000"); // Ensure this matches your backend

const Game = () => {
  const [showQuestion, setShowQuestion] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState(null);
  const [userAnswer, setUserAnswer] = useState(null);
  const [feedback, setFeedback] = useState(""); 
  const [spinKey, setSpinKey] = useState(0); // Key for forcing re-render
  const [score, setScore] = useState(0); // Track player score

  useEffect(() => {
    // Retrieve stored user details from localStorage
    const storedName = localStorage.getItem("name");
    const storedEmail = localStorage.getItem("email");

    if (!storedName || !storedEmail) {
      console.error("User not logged in. Score submission will fail.");
    }
  }, []);

  const handleSpinResult = (question) => {
    setCurrentQuestion(question);
    setShowQuestion(true);
    setUserAnswer(null); 
    setFeedback(""); 
    setSpinKey((prevKey) => prevKey + 1); // Change key to force re-render
  };

  const handleAnswerClick = (answer) => {
    setUserAnswer(answer);
    if (answer === currentQuestion.correctAnswer) {
      setFeedback("Correct! ðŸŽ‰");
      setScore((prevScore) => prevScore + 10); // Increase score by 10

      // Submit updated score to backend
      submitScore(score + 10);
    } else {
      setFeedback("Wrong answer. Try again! ðŸ˜•");
    }
  };

  const submitScore = (newScore) => {
    const playerName = localStorage.getItem("name");
    const playerEmail = localStorage.getItem("email");

    if (!playerName || !playerEmail) {
      console.error("User details missing, cannot submit score.");
      return;
    }

    const playerData = {
      name: playerName,
      email: playerEmail,
      score: newScore,
    };

    console.log("Submitting score:", playerData);
    socket.emit("submit-score", playerData);
  };

  return (
    <div className="game-container">
      <h1 className="game-title">Spin & Learn</h1>
      <p className="score-display">Your Score: {score}</p>
      <div className="game-content">
        <div className="spin-wheel-container">
          <SpinWheel key={spinKey} onSpin={handleSpinResult} /> {/* Force re-render */}
        </div>
        <div className="question-container">
          {showQuestion && currentQuestion && (
            <div className="question-box">
              <h2>{currentQuestion.question}</h2>
              <ul>
                {currentQuestion.options.map((option, index) => (
                  <li
                    key={index}
                    className={userAnswer === option ? "selected" : ""}
                    onClick={() => handleAnswerClick(option)}
                  >
                    {option}
                  </li>
                ))}
              </ul>
              <div className={`feedback ${feedback === "Correct! ðŸŽ‰" ? "correct" : "wrong"}`}>
                {feedback}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Game;
