import React, { useState } from "react";
import SpinWheel from "../  components/SpinWheel";
import "./Game.css";

const questions = {
  Math: "What is 5 + 3?",
  English: "What is the opposite of 'Hot'?",
  Science: "What planet is known as the Red Planet?",
  Puzzle: "What comes next? 2, 4, 6, __?",
  History: "Who was the first President of the USA?"
};

function Game() {
  const [currentQuestion, setCurrentQuestion] = useState(null);
  const [answer, setAnswer] = useState("");

  const handleSpin = (category) => {
    setCurrentQuestion(questions[category]);
  };

  const checkAnswer = () => {
    alert("Answer submitted!");
    setCurrentQuestion(null);
    setAnswer("");
  };

  return (
    <div className="container">
      <h1>EduSpin Game</h1>
      <SpinWheel onSpin={handleSpin} />
      {currentQuestion && (
        <div>
          <h3>{currentQuestion}</h3>
          <input type="text" value={answer} onChange={(e) => setAnswer(e.target.value)} />
          <button onClick={checkAnswer}>Submit</button>
        </div>
      )}
    </div>
  );
}

export default Game;
