import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, useLocation } from "react-router-dom";
import Home from "./pages/Home";
import Game from "./pages/Game";
import DrawLearn from "./pages/DrawLearn";
import PuzzleMatch from "./pages/PuzzleMatch";
import MathPlayground from "./pages/MathPlayground";
import StoryMode from "./pages/StoryMode";
import Leaderboard from "./pages/Leaderboard";
import VideoLearning from "./pages/VideoLearning";
import Dashboard from "./pages/Dashboard";
import Navbar from "./pages/Navbar"; // Import the Navbar component
import "./App.css";

function App() {
  const [showNavbar, setShowNavbar] = useState(false);
  const location = useLocation();

  useEffect(() => {
    if (location.pathname !== '/') {
      setShowNavbar(true);  // Show Navbar if not on Home page
    }
  }, [location]);

  return (
    <Router>
      {showNavbar && <Navbar />}  {/* Conditionally render Navbar */}
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/game" element={<Game />} />
        <Route path="/draw-learn" element={<DrawLearn />} />
        <Route path="/puzzle-match" element={<PuzzleMatch />} />
        <Route path="/math-playground" element={<MathPlayground />} />
        <Route path="/story-mode" element={<StoryMode />} />
        <Route path="/leaderboard" element={<Leaderboard />} />
        <Route path="/video-learning" element={<VideoLearning />} />
        <Route path="/dashboard" element={<Dashboard />} />
      </Routes>
    </Router>
  );
}

export default App;
