import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Game() {
  const [userDetails, setUserDetails] = useState({
    name: '',
    email: '',
  });
  const [score, setScore] = useState(0);

  // Get user details from localStorage when the component mounts
  useEffect(() => {
    const name = localStorage.getItem('name');
    const email = localStorage.getItem('email');

    if (name && email) {
      setUserDetails({ name, email });
    } else {
      console.error('User details missing!');
    }
  }, []);

  // Function to submit the score
  const submitScore = () => {
    if (!userDetails.name || !userDetails.email) {
      console.error('User details missing, cannot submit score');
      return;
    }

    // Assuming you have a socket connection or axios call to submit the score
    axios.post('http://localhost:5000/submit-score', {
      name: userDetails.name,
      email: userDetails.email,
      score: score,
    }).then(response => {
      console.log('Score submitted successfully', response);
    }).catch(error => {
      console.error('Error submitting score:', error);
    });
  };

  return (
    <div>
      <button onClick={submitScore}>Submit Score</button>
    </div>
  );
}

export default Game;
